def worker(flr, txt, idx, basement):
    flr += {"(": 1, ")": -1}.get(txt[0])

    if not (len(txt) - 1):
        return [flr, basement]

    if flr < 0 and not basement:
        basement = idx + 1

    return worker(flr, txt[1:], idx + 1, basement)


def starter(txt):
    flr, basement, idx = 0, False, 0
    return worker(flr, txt, idx, basement)


